	[3190102] = {
		unlock_by_stage_id = 0,
		name = "「超空间实验」",
		id = 3190102,
		iconPath = "TextureConfig/OperationDiary/OperationDiary_41102",
		desc = "\n项目全称「轴外分支空间演算实验」。\n\n依托比尔轴维系着人类文明通往唯一乐园的「主轴世界」，但在主轴之外，由于数据备份、逻辑网络随机性等原因，会常态性地衍生出不同于主轴世界的「分支」。这些分支之于盖亚通常属于冗余数据，因此盖亚会派出其造物从数据层面清除这些分支，避免主轴世界与分支相互观测及干扰。\n\n超空间实验正是利用这一机制，截取主轴世界上某一历史时间点的数据记录，人为创造出一条分支。但分支并不能从外部控制或观测，只能利用数据迁移技术将主轴世界的人员送入分支进行作业，例如寻找历史上被遗漏的秘密。由于人为创造分支需要消耗大量算力，实验只能选取高价值的局部区域进行具象构筑，分支的其余部分则以后台运算的方式维持。\n\n目前实验亟待解决的问题是如何应对盖亚的检测与清除，为迁移进入其中的人员提供更充足的作业时间。\n\n",
		position = {
			236,
			179.4,
			0
		}
	},
