	[317262042] = {
		speaker = "",
		prefab_name = "1093ui_story",
		content = "你只告诉我们那个叫做「常世」的异空间是伊邪那美的能力，并且她还是对抗鸦神的一方……难以理解，我们需要知道更多。",
